import React, { useState } from 'react';
import Whiteboard from './Whiteboard';
import io from 'socket.io-client';
import './index.css'; // Tailwind CSS

const socket = io('http://localhost:5000');

const App = () => {
  const [roomId, setRoomId] = useState('');
  const [username, setUsername] = useState('');
  const [isJoined, setIsJoined] = useState(false);

  const joinRoom = () => {
    if (roomId && username) {
      setIsJoined(true);
      socket.emit('joinRoom', { roomId, username });
    } else {
      alert("Please enter both Room ID and Username");
    }
  };

  return (
    <div className="h-screen flex items-center justify-center bg-gradient-to-r from-purple-500 to-blue-500">
      {!isJoined ? (
        <div className="bg-white rounded-lg shadow-lg p-10 text-center space-y-4 max-w-lg w-full">
          <h1 className="text-4xl font-bold text-indigo-600 mb-6">THINKBOARD</h1>
          <input
            type="text"
            placeholder="Enter your username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-400"
          />
          <input
            type="text"
            placeholder="Enter Room ID"
            value={roomId}
            onChange={(e) => setRoomId(e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-400"
          />
          <button
            onClick={joinRoom}
            className="w-full py-3 text-white bg-indigo-500 rounded-lg font-semibold hover:bg-indigo-600 transition duration-300"
          >
            Join Room
          </button>
        </div>
      ) : (
        <Whiteboard roomId={roomId} username={username} socket={socket} />
      )}
    </div>
  );
};

export default App;
